[" add-reminder.sh - Add a new reminder", "Usage: ./add-reminder.sh \"<message>\" \"<time>\" \"<priority>\"", "REMINDERS_FILE=\"", "SCRIPT_DIR=\"$(dirname \"", "MESSAGE=\"$1\"", "TIME=\"$2\"", "PRIORITY=\"$3\"", "if [ -z \"$MESSAGE\" ] || [ -z \"$TIME\" ]; then", "echo \"Usage: ./add-reminder.sh \\\"<message>\\\" \\\"<time>\\\" \\\"<priority>\\\"\"", "exit 1", "fi", "if [ -z \"$PRIORITY\" ]; then", "PRIORITY=\"medium\"", "fi", "echo \"Adding reminder: $MESSAGE at $TIME (priority: $PRIORITY)\"", "REMINDER_ID=\"rem-$(date +%s)\"", "if [ ! -f \"$REMINDERS_FILE\" ]; then", "echo '{\"reminders\": []}' > \"$REMINDERS_FILE\"", "fi", "jq --arg id \"$REMINDER_ID\" --arg msg \"$MESSAGE\" --arg time \"$TIME\" --arg pri \"$PRIORITY\"", ".reminders += [{id: $id, message: $msg, time: $time, priority: $pri, status: \"pending\"}]", "$REMINDERS_FILE\" > /tmp/reminders-temp.json && mv /tmp/reminders-temp.json \"$REMINDERS_FILE\"", "echo \"Reminder added successfully!\""]