[" check-reminders.sh - Check and trigger pending reminders", "REMINDERS_FILE=\"", "echo \"Checking reminders...\"", "CURRENT_TIME=\"$(date -u +\"", "if [ ! -f \"$REMINDERS_FILE\" ]; then", "echo \"No reminders file found.\"", "exit 0", "fi", "echo \"Current time: $CURRENT_TIME\"", "echo \"Due reminders:\": \"jq -r --arg now \"$CURRENT_TIME\"", ".reminders[] | select(.status == \"pending\" and .time <= $now)", "| \"- \\(.message) (priority: \\(.priority))\"", "$REMINDERS_FILE\""]