[" clear-reminder.sh - Clear completed reminders", "REMINDERS_FILE=\"", "if [ ! -f \"$REMINDERS_FILE\" ]; then", "echo \"No reminders file found.\"", "exit 0", "fi", "echo \"Clearing completed reminders...\"", "jq '.reminders |= map(select(.status != \"completed\"))'", "$REMINDERS_FILE\" > /tmp/reminders-clear.json && mv /tmp/reminders-clear.json \"$REMINDERS_FILE\"", "echo \"Completed reminders cleared.\"", "echo \"Active reminders:\": \"\"jq -r '.reminders[] | \"- \\(.message) (\\(.time))\"' \"$REMINDERS_FILE\""]